package com.keystone.sdk.demo

import androidx.test.ext.junit.runners.AndroidJUnit4
import com.keystone.sdk.KeystoneSDK

import org.junit.Test
import org.junit.runner.RunWith

import org.junit.Assert.*

@RunWith(AndroidJUnit4::class)
class KeystoneBitcoinSDKTest {
    @Test
    fun parsePSBT() {
        val sdk = KeystoneSDK(arrayOf(KeystoneSDK.ChainType.BTC))
        val sign = sdk.btc.parsePSBT("")
        assertEquals(
            "",
            sign.joinToString("") { "%02X".format(it) }
        )
    }

    @Test
    fun generatePSBT() {
        val sdk = KeystoneSDK(arrayOf(KeystoneSDK.ChainType.BTC))
        sdk.maxFragmentLen = 500
        val res = sdk.btc.generatePSBT("".chunked(2).map { it.toInt(16).toByte() }.toByteArray())
        assertEquals(
            res.nextPart(),
            "ur:crypto-"
        )
    }
}
