package com.keystone.sdk.demo

import androidx.test.ext.junit.runners.AndroidJUnit4
import com.keystone.module.HDKey
import com.keystone.sdk.KeystoneSDK

import org.junit.Test
import org.junit.runner.RunWith

import com.keystone.sdk.KeystoneSolanaSDK
import org.junit.Assert.*

@RunWith(AndroidJUnit4::class)
class KeystoneSDKTest {
    @Test
    fun decodeQR() {
        val sdk = KeystoneSDK()
        val ur = sdk.decodeQR("UR:CRYPTO-MULTI-ACCOUNTS/OTADCYJOKBWEJZAOLYTAADDLOXAOWKAXHDCXDPFNJYDNMKYTWDKTETWZHTATVDADNYJTSPWSBDAYWNHSJEFTFSDTFMCXBEPLYKRYAMTAADDYOTADLOCSDWYKCFADYKYKAEYKAEYKAOCYJOKBWEJZAXAAASIHGUGWGSDPDYAXISGRIHKKJKJYJLJTIHSSJYKSGT")
        assertEquals(
            "A3011A707EED6C0281D9012FA402F40358202D3C742B98F9EA7738F25A07E7019A6EC8EF0B08F1616B3A3D293E2010AEF5BD06D90130A30188182CF51901F5F500F500F5021A707EED6C03040965534F4C2D3003684B657973746F6E65",
            ur?.cbor
        )
        assertEquals(
            "crypto-multi-accounts",
            ur?.type
        )
    }

    @Test
    fun parseMultiAccounts() {
        val sdk = KeystoneSDK()
        val ur = sdk.decodeQR("UR:CRYPTO-MULTI-ACCOUNTS/OTADCYJOKBWEJZAOLYTAADDLOXAOWKAXHDCXDPFNJYDNMKYTWDKTETWZHTATVDADNYJTSPWSBDAYWNHSJEFTFSDTFMCXBEPLYKRYAMTAADDYOTADLOCSDWYKCFADYKYKAEYKAEYKAOCYJOKBWEJZAXAAASIHGUGWGSDPDYAXISGRIHKKJKJYJLJTIHSSJYKSGT")
        if (ur != null) {
            val accounts = sdk.parseMultiAccounts(ur.cbor)
            println(accounts)
            assertEquals(
                "Keystone",
                accounts.device
            )
            assertEquals(
                "707eed6c",
                accounts.masterFingerprint
            )
        }
    }

    @Test
    fun parseMultiAccountsWithXPub() {
        val sdk = KeystoneSDK()
        val accounts = sdk.parseMultiAccounts("a3011aa424853c0281d9012fa4035821034af544244d31619d773521a1a366373c485ff89de50bea543c2b14cccfbb6a500458208dc2427d8ab23caab07729f88f089a3cfa2cfffcd7d1e507f983c0d44a5dbd3506d90130a10186182cf500f500f5081a149439dc03686b657973746f6e65")
        println(accounts)
        assertEquals(
            "8dc2427d8ab23caab07729f88f089a3cfa2cfffcd7d1e507f983c0d44a5dbd35",
            accounts.keys[0].getChainCode()
        )
        assertEquals(
            "xpub6BoYPFH1MivLdh2BWZuRu6LfuaVSkVak5wsDxjjkAWcUM2QPKyeCHXMgDfRJFvKZhqA4vM5vsgcD6C5ot9eThnFHstgPntNzBLUdLeKS7Zt",
            accounts.keys[0].getExtendedPublicKey()
        )
    }

    @Test
    fun parseSourceHDKey() {
        val sdk = KeystoneSDK()
        val hdKey = sdk.parseSourceHDKey("")
        println(hdKey)

        val expectedResult = HDKey(
            "032f547fd525b6d83cc2c44f939cc1425fa1e98d97d26b00f9e2d04952933c5128",
            "b92b17b393612fc8e945e5c5389439ca0c0a28c3076c060b15c3f9f6523a9d19",
            "52006ea0",
            "account.standard"
        )

        assertEquals(expectedResult, hdKey)
    }

    @Test
    fun parseHDKeys() {
        val sdk = KeystoneSDK()
        val multiHdHeys = sdk.parseHDKeys("")
        println(multiHdHeys)

        val theFirstExpectedHdKey = HDKey(
            "02fef03a2bd3de113f1dc1cdb1e69aa4d935dc3458d542d796f5827abbb1a58b5e",
            "",
            "52006ea0",
            "account.ledger_live"
        )

        assertEquals(10, multiHdHeys.hdKeys.size)
        assertEquals(theFirstExpectedHdKey, multiHdHeys.hdKeys.first())
    }

    @Test
    fun getUncompressedKey() {
        val sdk = KeystoneSDK()
        val uncompressedKey = sdk.getUncompressedKey("02fef03a2bd3de113f1dc1cdb1e69aa4d935dc3458d542d796f5827abbb1a58b5e")
        val expectedResult = "04fef03a2bd3de113f1dc1cdb1e69aa4d935dc3458d542d796f5827abbb1a58b5ebdffecfa6587da3216d50114700e5e314650cc2268e9fcb6ac31593bcc71d178"

        assertEquals(expectedResult, uncompressedKey)
    }
}
